'use strict';
//==============================================================================
//  Modules
//==============================================================================

var userRepository = require('AGL/user/repository');

//==============================================================================
//  Public Functions
//==============================================================================

// TODO Should validation happen at this layer?

module.exports.CreateUser = function (args, callback) {
    if (typeof callback !== 'function') {
        throw ("Callback argument must be a function.");
    }

    userRepository.NewUser(args, callback); // TODO do we need this whole layer at all?
                                                  // The whole point of this module was to move logic
                                                  // out of the routes module; maybe try to do that more.
};

module.exports.GetUser = function (args, callback) {
    if (typeof callback !== 'function') {
        throw ("Callback argument must be a function.");
    }

    var public_id = args.public_id;
    userRepository.GetUser(public_id, callback); // TODO do we need this whole layer at all?
                                                  // The whole point of this module was to move logic
                                                  // out of the routes module; maybe try to do that more.
};

