'use strict';
//==============================================================================
//  Modules
//==============================================================================

var userRepository = require('AGL/user/repository');

//==============================================================================
//  Public Functions
//==============================================================================

module.exports.CreateUser = function (args, callback) {
    if (typeof callback !== 'function') {
        throw ("Callback argument must be a function.");
    }

    userRepository.NewUser(args, callback);
};

module.exports.GetUser = function (args, callback) {
    if (typeof callback !== 'function') {
        throw ("Callback argument must be a function.");
    }

    var public_id = args.public_id;
    userRepository.GetUser(public_id, callback);
};

//==============================================================================
//  Private Functions
//==============================================================================

