var userRepository = require('AGL/user/repository');

//==============================================================================
//  Public Functions
//==============================================================================

module.exports.CreateUser = function (properties, callback) {
    if (typeof callback !== 'function') {
        throw ("Callback argument must be a function.");
    }

    userRepository.NewUser(properties, callback); // TODO do we need this whole layer at all?
                                                  // The whole point of this module was to move logic
                                                  // out of the routes module; maybe try to do that more.
};

