'use strict';
//==============================================================================
//  Modules
//==============================================================================

var async       = require('async');

var dbAccessor  = require('AGL/database/dbAccessor');
var userFactory = require('AGL/user/factory');
var validate    = require('validate.js');

//==============================================================================
//  Constants
//==============================================================================

var TABLE_NAME = 'users';

//==============================================================================
//  Public Functions
//==============================================================================

/* 
 *  public undefined SaveUser(user, callback)
 *
 *  Saves a new or existing user to the database.
 *  Returns the user's username to the callback if save is successfull.
 *
 *  @param {object} user object, generated from a factory.
 *  @param {function} callback
 */
module.exports.SaveUser = function (user, callback) {
	if (!validate.isFunction(callback)) {
		throw new Error('callback argument must be a function.');
	}

	async.waterfall([
		function QueryExistingUser (callback) {
			QueryUser(user.GetUsername(), callback);
		},
		function InsertOrUpdateUser (existingUsers, callback) {
			if (existingUsers.length >= 1) {
				UpdateUser(user, callback);
			}
			else {
				InsertUser(user, callback);
			}
		}
	],
	function UserSaved (error, users) {
		if (error) {
			return callback(error); // TODO This does not work
		}
		else if (users.length === 0) {
			return callback('Query did not return a value. Unknown reason.');
		}
		else {
			return callback(null, user.GetUsername());
		}
	});

};

/* public undefined GetUser(username, callback)
 *
 *  Gets a user from the database based on username.
 *  Returns a user object to the callback if succussfull.
 *
 *  @param {string} searchValue - either username or email
 *  @param {function} callback
 */
module.exports.GetUser = function (searchValue, callback) {
	if (!validate.isFunction(callback)) {
		throw new Error('callback argument must be a function.');
	}

	QueryUser(searchValue, function QueryResult (error, users) {
		if (error) {
			return callback(error);
		}
		else {
			// TODO build user and return it
		}
	});

};

//==============================================================================
//  Private Functions
//==============================================================================

// TODO DOCO
function QueryUser (searchValue, callback) {
	dbAccessor.GetQueryBuilder()(TABLE_NAME)
	.select('username', 'password', 'email', 'created', 'lastModified')
	.where('username', searchValue)
	.orWhere('email', searchValue)
	.limit(1)
	.asCallback(callback);
}

// TODO DOCO
function InsertUser (user, callback) {
	dbAccessor.GetQueryBuilder()(TABLE_NAME)
	.insert({
		username : user.GetUsername(),
		password : user.GetPasswordHash(),
		email    : user.GetEmail()
	})
	.asCallback(callback);
}

// TODO DOCO
function UpdateUser (user, callback) {
	dbAccessor.GetQueryBuilder()(TABLE_NAME)
	.update({
		username : user.GetUsername(),
		password : user.GetPasswordHash(),
		email    : user.GetEmail()
	})
	.where('username', user.GetUsername())
	.asCallback(callback);
}

// TODO DOCO
function DeleteUser (username, callback) {
	dbAccessor.GetQueryBuilder()(TABLE_NAME)
	.del()
	.where('username', username)
}

